mapper.select:
		select * 
		from (
		 select *
		from attraction

			where tour_name LIKE CONCAT('%',#{tour_name},'%')

		 LIMIT #{endRow} OFFSET #{startRow}
    	) AS subquery

select filename
from attraction
where tour_code = #{tour_code}

mapper.update:
UPDATE attraction
SET area_code=#{area_code}
   ,tour_name=#{tour_name}
   ,keyword=#{keyword}
   ,content=#{content}
   ,filename=#{filename}
   ,filesize=#{filesize}           
WHERE tour_code=#{tour_code}
